const esAlfanumerico=e=>/[a-z0-9]/.test(e),esPalindromo=e=>{const t=e.toLowerCase().split("");return t.filter(esAlfanumerico).join("")===t.filter(esAlfanumerico).reverse().join("")},verificarPalindromo=()=>{const e=document.getElementById("textoInput").value.trim(),t=document.getElementById("resultado");if(!e)return t.textContent="Por favor, ingresa un texto",void(t.className="resultado");const o=esPalindromo(e);t.textContent=o?"¡Es un palíndromo!":"No es un palíndromo",t.className=`resultado ${o?"es-palindromo":"no-palindromo"}`};document.getElementById("textoInput").addEventListener("input",verificarPalindromo);